import React from 'react';

class EditFishForm extends React.Component {

  // alternative to refs way
  handleChange = (event) => {
    console.log(event.currentTarget.name);
    // computed something instead of manually writing changes for all props
    // const updatedFish = {
    //   ...this.props.fish,
    //   [event.currentTarget.name]: event.currentTarget.value
    // }
    // console.log(updatedFish);
  }

  render() {
    return (
    <div className="fish-edit">
      <input
        type="text"
        name="name"
        // needs onChange event so user can type into input field (React rule)
        onChange={this.handleChange}
        value={this.props.name}
      />
      <input
        type="text"
        name="price"
        onChange={this.handleChange}
        value={this.props.price}
      />
      <select
        type="type"
        onChange={this.handleChange}
        name="status"
        value={this.props.status}
      >
        <option type="available">Fresh!</option>
        <option type="unavailable">Sold Out!</option>
      </select>
      <textarea
        name="desc"
        value={this.props.desc}
        onChange={this.handleChange}
        >
      </textarea>
      <input type="text"
        name="image"
        onChange={this.handleChange}
        value={this.props.image}
      />
    </div>
    )
  }
}

export default EditFishForm;
