import React from "react";
import PropTypes from "prop-types";
import firebase from "firebase";
import AddFishForm from "./AddFishForm";
import EditFishForm from "./EditFishForm";
import Login from "./Login";
import base, { firebaseApp } from "../base";    // import named and default

class Inventory extends React.Component {

  static propTypes = {
    fishes: PropTypes.object,
    updateFish: PropTypes.func,
    deleteFish: PropTypes.func,
    loadSampleFishes: PropTypes.func,
    addFish: PropTypes.func
  }

  state = {
    uid: null,
    owner: null,
  }

  authHandler = async (authData) => {
    // 1. look up durrent store in FB DB
    console.log("authData", authData);
    // fetch current store. fetch returns a promise
    // to put the store in the variable instead of the promise, add "await" to start
    const store = base.fetch(this.props.storeId, { context: this });
    console.log("store", store);

    // 2 .claim store if there's no owner
    if(!store.owner) {
      // save it as our own: opposite of fetch, post/ push to firebase owner field
      await base.post(`${this.props.storeId}/owner`, {
        data: authData.user.uid         // uid is user github auth token
      })
    }

    // 3. set state of inventory component
    this.setState({
      uid: authData.user.uid,
      owner: store.owner || authData.user.uid
      // who is currently logged in user and who is owner of store?
      // if same, proceed, if different, you ca't log in error msg.
    })

  }

  // disny work: props.authenticate not a function
  authenticate = provider => {
  console.log('provider', provider)
  // dynamically look up provider. Parenthesis at end to call function.
  const authProvider = new firebase.auth[`${provider}AuthProvider`]();
  // connect to Auth bit of firebase, sign in, then call AuthHAndler
  firebaseApp
    .auth()
    .signInWithPopup(authProvider)
    .then(this.authHandler);
    // provider is "github", "faceook" "twitter"

};

  render() {
    const logout = <button onClick={this.logout}>Log Out</button>


    // 1. check if user logged on?
    if (!this.state.uid) {
      return <Login authenticate={this.authenticate}/>
    }

    // 2. check if user is NOT the owner of the store
    if(!this.state.uid !== this.state.owner) {
      return <div>
        <p>Sorry, you are not the owner of this store.</p>
      </div>
    }

    // 3. If user is owner, render Inventory.
    return (
    <div className="inventory">
      <h2>Inventory</h2>
      {/* loop over fish state and render an edit fish form element for each fish */}
      {Object.keys(this.props.fishes).map( key => (
        // key of each fish
        <EditFishForm
          key={key}
          index={key}
          fish={this.props.fishes[key]}
          updateFish={this.props.updateFish}
          deleteFish={this.props.deleteFish}
        />
      ))}
      <AddFishForm addFish={this.props.addFish}/>
      <button onClick={this.props.loadSampleFishes}
      >
        Load Sample Fishes
      </button>
    </div>
    )
  }
}

export default Inventory;
