import React from 'react';
import { getFunName } from '../helpers';

class StorePicker extends React.Component {

  handleSubmit(event) {
    // prevents page submit / refresh
    event.preventDefault()
    console.log('go to store');
  }

  render() {
    return (
      // listening for form submit, NOT button click!!
      // no () so method isn't called when component mounts.
      <form className="store-selector" onSubmit={this.handleSubmit}>
        <h2>Please Enter a Store</h2>
        {/* runs getFunName when component mounts */}
        <input
          type="text"
          required
          placeholder="Store Name"
          defaultValue={getFunName()}
        />
        <button type="submit">Visit Store -> </button>
      </form>
    )
  }
}

export default StorePicker;
