import React from 'react';

class EditFishForm extends React.Component {
  render() {
    return (
    <div className="fish-edit">
      <input
        type="text"
        name="name"
        onChange={this.handleChange}
        value={this.props.name}/>
      <input type="text" name="price" value={this.props.price}/>
      <select type="type" name="status" value={this.props.status}>
        <option type="available">Fresh!</option>
        <option type="unavailable">Sold Out!</option>
      </select>
      <textarea name="desc" value={this.props.desc}></textarea>
      <input type="text" name="image" value={this.props.image}/>
    </div>
    )
  }
}

export default EditFishForm;
